import React, { useEffect, useState } from 'react';
import { GoogleMap, useLoadScript, Marker } from '@react-google-maps/api';
import axios from 'axios';

const mapContainerStyle = {
  width: '100vw',
  height: '100vh',
};

const Map = ({ handleLogout }) => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
    libraries: ['places'],
  });

  const [userData, setUserData] = useState(null);
  const [userLocation, setUserLocation] = useState({ lat: 0, lng: 0 });
  const [manualLocation, setManualLocation] = useState({ lat: '', lng: '' });
  const [isManual, setIsManual] = useState(false);
  const [places, setPlaces] = useState([]);
  const [placeName, setPlaceName] = useState('');

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          console.error('No token found');
          return;
        }
        console.log('Token:', token);
        const res = await axios.get('http://localhost:5000/users/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUserData(res.data);
      } catch (error) {
        if (error.response) {
          console.error('Error fetching user data:', error.response.data);
        } else if (error.request) {
          console.error('No response received:', error.request);
        } else {
          console.error('Error setting up request:', error.message);
        }
      }
    };

    fetchUserData();
  }, []);

  useEffect(() => {
    if (!isManual && navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const newLocation = {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          };
          setUserLocation(newLocation);

          try {
            const token = localStorage.getItem('token');
            if (!token) {
              console.error('No token found');
              return;
            }
            await axios.post('http://localhost:5000/users/update-location', newLocation, {
              headers: { Authorization: `Bearer ${token}` },
            });
          } catch (error) {
            if (error.response) {
              console.error('Error updating location:', error.response.data);
            } else if (error.request) {
              console.error('No response received:', error.request);
            } else {
              console.error('Error setting up request:', error.message);
            }
          }
        },
        (error) => {
          console.error('Geolocation error:', error);
        },
        {
          enableHighAccuracy: true,
        }
      );
    }
  }, [isManual]);

  const handleManualSubmit = async (e) => {
    e.preventDefault();
    const newLocation = {
      lat: parseFloat(manualLocation.lat),
      lng: parseFloat(manualLocation.lng),
    };
    setUserLocation(newLocation);

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        console.error('No token found');
        return;
      }
      await axios.post('http://localhost:5000/users/update-location', newLocation, {
        headers: { Authorization: `Bearer ${token}` },
      });
    } catch (error) {
      if (error.response) {
        console.error('Error updating location manually:', error.response.data);
      } else if (error.request) {
        console.error('No response received:', error.request);
      } else {
        console.error('Error setting up request:', error.message);
      }
    }
  };

  const findPlaces = async () => {
    try {
      const res = await axios.get(
        `https://maps.googleapis.com/maps/api/place/textsearch/json`,
        {
          params: {
            query: placeName,
            key: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
          },
        }
      );

      console.log('Nearby places:', res.data.results);
      setPlaces(res.data.results);
      if (res.data.results.length > 0) {
        setUserLocation({
          lat: res.data.results[0].geometry.location.lat,
          lng: res.data.results[0].geometry.location.lng,
        });
      }
    } catch (error) {
      if (error.response) {
        console.error('Error fetching nearby places:', error.response.data);
      } else if (error.request) {
        console.error('No response received:', error.request);
      } else {
        console.error('Error setting up request:', error.message);
      }
    }
  };

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading Maps</div>;

  return (
    <div>
      {userData && <div>Welcome, {userData.email}</div>}
      <button onClick={handleLogout}>Log Out</button>
      <button onClick={() => setIsManual(!isManual)}>
        {isManual ? 'Use Geolocation' : 'Enter Location Manually'}
      </button>
      {isManual && (
        <form onSubmit={handleManualSubmit}>
          <input
            type="number"
            value={manualLocation.lat}
            onChange={(e) => setManualLocation({ ...manualLocation, lat: e.target.value })}
            placeholder="Latitude"
            required
          />
          <input
            type="number"
            value={manualLocation.lng}
            onChange={(e) => setManualLocation({ ...manualLocation, lng: e.target.value })}
            placeholder="Longitude"
            required
          />
          <button type="submit">Update Location</button>
        </form>
      )}
      <div>
        <input
          type="text"
          value={placeName}
          onChange={(e) => setPlaceName(e.target.value)}
          placeholder="Enter place name"
        />
        <button onClick={findPlaces}>Find Nearby Places</button>
      </div>
      <GoogleMap mapContainerStyle={mapContainerStyle} zoom={12} center={userLocation}>
        <Marker position={userLocation} />
        {places.map((place, index) => (
          <Marker
            key={index}
            position={{
              lat: place.geometry.location.lat,
              lng: place.geometry.location.lng,
            }}
            label={place.name}
          />
        ))}
      </GoogleMap>
    </div>
  );
};

export default Map;
