import React, { useEffect, useState } from 'react';
import { GoogleMap, useLoadScript, Marker } from '@react-google-maps/api';
import axios from 'axios';

const mapContainerStyle = {
  width: '100vw',
  height: '100vh',
};

const Map = () => {
  const { isLoaded, loadError } = useLoadScript({
    googleMapsApiKey: process.env.REACT_APP_GOOGLE_MAPS_API_KEY,
  });

  const [userData, setUserData] = useState(null);
  const [userLocation, setUserLocation] = useState({ lat: 0, lng: 0 });
  const [manualLocation, setManualLocation] = useState({ lat: '', lng: '' });
  const [isManual, setIsManual] = useState(false);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const token = localStorage.getItem('token');
        const res = await axios.get('http://localhost:5000/users/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUserData(res.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchUserData();
  }, []);

  useEffect(() => {
    if (!isManual && navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const newLocation = {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          };
          setUserLocation(newLocation);

          try {
            const token = localStorage.getItem('token');
            await axios.post('http://localhost:5000/users/update-location', newLocation, {
              headers: { Authorization: `Bearer ${token}` },
            });
          } catch (error) {
            console.error(error);
          }
        },
        (error) => {
          console.error(error);
        },
        {
          enableHighAccuracy: true,
        }
      );
    }
  }, [isManual]);

  const handleManualSubmit = async (e) => {
    e.preventDefault();
    const newLocation = {
      lat: parseFloat(manualLocation.lat),
      lng: parseFloat(manualLocation.lng),
    };
    setUserLocation(newLocation);

    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5000/users/update-location', newLocation, {
        headers: { Authorization: `Bearer ${token}` },
      });
    } catch (error) {
      console.error(error);
    }
  };

  if (loadError) return <div>Error loading maps</div>;
  if (!isLoaded) return <div>Loading Maps</div>;

  return (
    <div>
      {userData && <div>Welcome, {userData.email}</div>}
      <button onClick={() => setIsManual(!isManual)}>
        {isManual ? 'Use Geolocation' : 'Enter Location Manually'}
      </button>
      {isManual && (
        <form onSubmit={handleManualSubmit}>
          <input
            type="number"
            value={manualLocation.lat}
            onChange={(e) => setManualLocation({ ...manualLocation, lat: e.target.value })}
            placeholder="Latitude"
            required
          />
          <input
            type="number"
            value={manualLocation.lng}
            onChange={(e) => setManualLocation({ ...manualLocation, lng: e.target.value })}
            placeholder="Longitude"
            required
          />
          <button type="submit">Update Location</button>
        </form>
      )}
      <GoogleMap mapContainerStyle={mapContainerStyle} zoom={8} center={userLocation}>
        <Marker position={userLocation} />
      </GoogleMap>
    </div>
  );
};

export default Map;
